#define ll long long

class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll c = 0;
        unordered_map<ll, ll> pr;
        for(int i=0;i<n;i++){
            unordered_map<ll, ll> cr;
            cr[nums[i]]=1;
            if(nums[i]==k) c++;
            for(auto& [x,y] : pr){
                ll nr = x & nums[i];
                if(nr == k) c+=y;
                cr[nr] += y;
            }
            pr = cr;
        }
        return c;
    }
};
