class Solution {
public:
    long long solve(vector<int>& nums, int k)
    {
        int n=nums.size();

        int i=0,j=0;
        vector<int> cnt1(32,0);

        long long res=0;
        int last=-1;

        while(j<n)
        {
            for(int k=0;k<31;k++)
                if(nums[j]&(1<<k))
                   cnt1[k]++;

            int t=0;
            for(int k=0;k<31;k++)
                if(cnt1[k]==(j-i+1))
                    t|=(1<<k);

            // cout<<t<<endl;

            while(i<=j && t<k)
            {
                for(int k=0;k<31;k++)
                    if(nums[i]&(1<<k))   cnt1[k]--;

                i++;

                t=0;
                for(int k=0;k<31;k++)
                    if(cnt1[k]==(j-i+1))
                        t|=(1<<k);

            }


            if(t>=k)    res+=j-i+1;

            j++;
        }

        return res;

    }
    long long countSubarrays(vector<int>& nums, int k) {
        return solve(nums,k)-solve(nums,k+1);
    }
};
