class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int size=nums.size();
        long long t=0;
        unordered_map<long long,long long>pr;
        for(int i=0;i<size;++i){
            unordered_map<long long,long long>cr;
            if(nums[i]==k){
                ++t;
            }
            cr[nums[i]]=1;
            for(auto &[v,f]:pr){
                long long nwr=v&nums[i];
                if(nwr==k){
                    t+=f;
                }
                cr[nwr]+=f;
            }
            pr=cr;
        }
        return t;
    }
};
