class Solution {
    public long countSubarrays(int[] nums, int k) {
      int num = nums.length;
      long c1 = 0;
      HashMap<Long,Long> hm = new HashMap<>();
      for(int i = 0;i < num;i++){
        HashMap<Long,Long> hm1 = new HashMap<>();
        if(nums[i] == k){
          c1++;
        }
        hm1.put((long)nums[i],1L);

        for(var en : hm.entrySet()){
          long x = en.getKey() & ((long)nums[i]);
          if(x == k){
            c1 += en.getValue();
          }
          hm1.merge(x,en.getValue(),Long::sum);
        }
        hm = hm1;
      }
      return c1;
    }
}
