class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n=len(nums)
        c=0
        b={}
        for i in range(n):
            t={}
            if nums[i]==k:
                c=c+1
            t[nums[i]]=1
            for p,q in b.items():
                r=p&nums[i]
                if r==k:
                    c=c+q
                if r in t:
                    t[r]=t[r]+q
                else:
                    t[r]=q
            b=t
        return c
