class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
         long long count =0 ;
         unordered_map<long long,long long>mp;
         for(auto i : nums){
            unordered_map<long long,long long>temp;
            if(i==k)count++;
            temp[i]++;
            for(auto j : mp){
                long long val = j.first & i;
                if(val==k){
                    count += j.second;
                }
                temp[val]+= j.second;
            }
            mp=temp;
         }
        return count;
    }
};
