class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long n = nums.size();
        long long xx = 0;
        map<long long, long long> mpp,mp;
        mpp[xx]++;
        long long cnt=0,cnt2=0;
        for (int i = 0; i < n; ++i) {
            map<long long, long long> temp; 
            xx = xx & nums[i];
            int x = xx & k;
            if (nums[i] == k) {
                cnt++;
            }
            cnt2 += mpp[x];
            temp[nums[i]] = 1;
            for (auto& x : mp) {
                mpp[x.first]--;
                long long x1 = x.first & nums[i];
                if (x1 == k) {
                    cnt += x.second;
                }
                else{
                    cnt2+=x.second;
                }
                mpp[x1] += x.second;
                temp[x1] += x.second;
            }
            mpp[xx]++;
            mp = temp;
        }
        return cnt;
    }
};
