func countSubarrays(nums []int, k int) int64 {

	type pair struct{ x, cnt int }
	ls := make([]pair, 0)

	ans := 0
	for _, v := range nums {

		ls = append(ls, pair{v, 1})

		nls := ls
		ls = nil

		for _, t := range nls {

			cur := t.x & v
			if cur == k {
				ans += t.cnt
			}
			if len(ls) > 0 && ls[len(ls)-1].x == cur {
				ls[len(ls)-1].cnt += t.cnt
			} else {
				ls = append(ls, pair{cur, t.cnt})
			}
		}

	}
	return int64(ans)

}
