#define ll long long int 



class Solution {
public:


ll solve(vector<int>& nums, int k){

 ll x= nums.size();
 ll cnt=0;

 unordered_map<ll,ll>prev;

 for(int i=0;i<x;i++){
    unordered_map<ll,ll>cur;

    if(nums[i]==k){
        cnt++;
    }

    cur[nums[i]]=1;



 for (auto & it:prev){

     ll val= it.first;
      ll freq=it.second;

    ll nwAans= val & nums[i];
    if(nwAans==k){
        cnt+=freq;
    }
    cur[nwAans]+=freq;
 }
 prev=cur;

 }

 return cnt;

}
    long long countSubarrays(vector<int>& nums, int k) {


   ll ans=solve(nums,k);
return ans;


    }
};
