class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long cnt=0;
        unordered_map<int,int>mp;
        for(int i=0;i<nums.size();i++){
            unordered_map<int,int>mp1;
            if(nums[i]==k){
                cnt++;
            }
            mp1[nums[i]]=1;
            for(auto x:mp){
                int u=x.first&nums[i];
                if(u==k){
                    cnt=cnt+x.second;
                }
                if(mp1.find(u)!=mp1.end()){
                    mp1[u]=mp1[u]+x.second;
                }
                else{
                    mp1[u]=x.second;
                }
            }
            mp.clear();
            for(auto x:mp1){
                mp[x.first]=x.second;
            }
        }
        return cnt;


    }
};
