        unordered_map<long long,long long> mp;
        for(int i=0;i<nums.size();i++){
        unordered_map<long long,long long> pm;
            if(nums[i]==k) cnt++;
            pm[nums[i]]=1;

            for(auto& [key,value]:mp){
                long long res=key & nums[i];
                if(res==k) cnt+=value;
                pm[res]+=value;
            }
            mp=pm;
        }
        for(int i=1;i<100;i++){}

        return cnt;
    }
};
