class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        unordered_map < int , int > frq;
        int n = nums.size();
        for(int i = 0;i<n;++i) {
            unordered_map < int , int > tfrq;
            for(auto &a : frq) {
                if((a.first&nums[i]) >= k) {
                    tfrq[(a.first&nums[i])] += a.second;
                }
            }
            if(nums[i] >= k) tfrq[nums[i]] += 1;
            ans += 1ll * tfrq[k];
            frq = tfrq;
        }
        return ans;
    }
};
