class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<long ,long>mp;
        long cnt=0, n=nums.size();
        for(int i=0;i<n;i++){
            unordered_map<long ,long>mp2;
            if(nums[i]==k){
                cnt++;
            }
            mp2[nums[i]]=1;
            for(auto it:mp){
                long x=it.first&nums[i];
                if(x==k){
                    cnt+=it.second;
                }
                mp2[x]+=it.second;
            }
            mp=mp2;
        }
        return cnt;
    }
};
