class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long total =0;
        Map<Long, Long> prev = new HashMap<>();
        for(int i =0; i<n; i++){
            Map<Long, Long> curr = new HashMap<>();
            if(nums[i]==k){
                total++;
            }
            curr.put((long)nums[i],1L);
            for(Map.Entry<Long, Long> entry : prev.entrySet()){
                long newResult = entry.getKey()& nums[i];
                if(newResult == k ){
                    total+=entry.getValue();
                }
                curr.put(newResult, curr.getOrDefault(newResult, 0L) + entry.getValue());
            }
            prev = curr;
        }
        return total;
    }
}
