class Solution {
public:

    long long f(vector<int>&v,int k){
        int n = v.size();
        int ans =0;
        long long cn =0;
        vector<vector<int>>cnt(n+1,vector<int>(32,0));
        long long i =0;
        long long j =1;

        while(j<=n){

            int num = v[j-1];
            int l=0;

            for(int p=0;p<32;p++){

                 if((num&(1ll<<p)) ==0){
                    cnt[j][p]=cnt[j-1][p]+1;
                }
                else{
                    cnt[j][p]=cnt[j-1][p];
                }

            }
            ans =0;
            for(int p=0;p<32;p++){

                if(cnt[j][p]-cnt[i][p] ==0){
                    ans|=(1ll<<p);
                }
            }



            while(ans<k && i<j){

                i++;
                ans =0;
                for(int p=0;p<32;p++){

                    if(cnt[j][p]-cnt[i][p] ==0){
                        ans|=(1ll<<p);
                    }

                }



            }

            if(ans>=k){
                cn+=(j-i);
            }

            j++;

        }

        return cn;

    }

    long long countSubarrays(vector<int>& nums, int k) {


        long long cnt1 = f(nums,k);
        long long cnt2 = f(nums,k+1);

        //cout<<cnt1<<" "<<cnt2<<endl;
        return cnt1-cnt2;
    }
};
