class Solution {
    public long countSubarrays(int[] nums, int k) {
        long countArobh = 0;
        HashMap<Integer, Integer> andMap = new HashMap<>();

        for (int num : nums) {
            HashMap<Integer, Integer> newAndMap = new HashMap<>();
            if (num == k) {
                countArobh++;
            }
            newAndMap.put(num, newAndMap.getOrDefault(num, 0) + 1);

            for (int key : andMap.keySet()) {
                int newAnd = key & num;
                if (newAnd == k) {
                    countArobh += andMap.get(key);
                }
                newAndMap.put(newAnd, newAndMap.getOrDefault(newAnd, 0) + andMap.get(key));
            }
            andMap = newAndMap;
        }

        return countArobh;
    }
}
