class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0;
        int n=nums.size();
        unordered_map<int,long long> hashmap={{nums[0],1}};
        for (int i=1;i<n;i++){
            unordered_map<int,long long> tmp={{nums[i],1}};
            for (const auto& pair:hashmap) {
                int key=pair.first & nums[i];
                if (pair.first==k)
                    ans+=pair.second;
                auto it = tmp.find(key);
                if (it!= tmp.end())
                    tmp[key]=it->second+pair.second;
                else
                    tmp[key]=pair.second;
            }
            hashmap=tmp;
        }
        auto it=hashmap.find(k);
        if (it!= hashmap.end())
            ans+=it->second;
        return ans;
    }
};
