class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long n=nums.size(),ans=0;
        map<long long,long long>lastwindow;
        for(int i=0;i<n;i++)
        {
            ans+=nums[i]==k;
            map<long long,long long>thiswindow;
            thiswindow[nums[i]]++;
            for(auto &it:lastwindow)
            {
                thiswindow[(it.first&nums[i])]+=it.second;
                ans+=it.second*((it.first&nums[i])==k);
            }
            lastwindow=thiswindow;
        }
        return ans;
    }
};
