#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll akash = 0;

        map<ll, ll> mp;
        for(int i = 0; i <n; i++){
            map<ll, ll> mp2;
            if(nums[i] == k) akash++;

            mp2[nums[i]] = 1;
            for(auto &it: mp){
                ll temp = (it.first & nums[i]);
                if(temp == k){
                    akash += it.second;
                }
                mp2[temp] += it.second;
            }
            mp = mp2;
        }

        return akash;
    }
};
