class Solution {
public:
    long long countSubarrays(vector<int>& v, int k) {
         int n = v.size();
        long long t = 0;
        unordered_map<long long, long long> prev;
        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> curr; 
            if (v[i] == k) {
                t++;
            }
            curr[v[i]] = 1;
            for (auto& [val, freq] : prev) {
                long long nand = val & v[i];
                if (nand == k) {
                    t += freq;
                }
                curr[nand] += freq;
            }

            prev = curr;
        }
        return t;
    }
};
