class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long ans=0;
        for(auto aa:nums){
            if(aa==k) ans++;
        }
        unordered_map<long long,long long> dp;
        for(auto aa:nums){
            unordered_map<long long,long long> mp;
            mp[aa]++;
            for(auto a:dp){
                if((a.first&aa)==k) ans+=a.second;
                mp[a.first&aa]+=a.second;
            }
            dp=mp;
        }
        return ans;
    }
};
