class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<long long, long long> m;
        long long ans = 0;
        int n = nums.size();
        int i=0;
        while(i<n) {
            unordered_map<long long, long long> c; 
            c[nums[i]] = 1;
            if (nums[i] == k) {
                ans++;
            }
            for (auto it : m) {
                long long fin = nums[i] & it.first;
                c[fin] += it.second;
                if (fin == k) {
                    ans += it.second;
                }
            }
            i++;
            m = c;
        }
        return ans;
    }
};
