class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long cnt=0;
        int n=nums.size();


        //to store the prev results
        unordered_map<long long,long long> preVal;

        for(int i=0;i<n;++i){
            unordered_map<long long,long long> curVal;

            //store the first result
            if(nums[i]==k){
                ++cnt;
            }

            //put the freq
            curVal[nums[i]]=1;//value and its corresponding freq

            for(auto &[data,freq]:preVal){
                long long curAnd=data&nums[i];
                curVal[curAnd]+=freq;
                if(curAnd==k){
                    cnt+=freq;
                }

            }
            preVal=curVal;
        }
        return cnt;
    }
};
