class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        int n = nums.size();

        unordered_map<long long , long long> mp;

        long long ans = 0;

        for(int i = 0; i < n; i++)
        {
            unordered_map<long long , long long> mp1;

            mp1[nums[i]] = 1;

            if(nums[i] == k)
            {
                ans++;
            }

            for(auto itr : mp)
            {
                long long x = (itr.first & nums[i]);

                mp1[x] += (itr.second);

                if(x == k)
                {
                    ans += (itr.second);
                }


            }

            mp = mp1;


        }

        return ans;
    }
};
