class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        ans = 0
        for i, x in enumerate(nums):
            if x == k:
                ans += 1
            j = i - 1
            while j >= 0 and nums[j] & x != nums[j]:
                nums[j] &= x
                j -= 1
            ll = bisect_left(nums, k, lo=0, hi=i)
            rr = bisect_right(nums, k, lo=0, hi=i)
            if rr - ll > 0:
                ans += rr - ll
        return ans
