import java.util.HashMap;
import java.util.Map;

class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long cnt = 0;

        Map<Long, Long> pr = new HashMap<>();

        for (int i = 0; i < n; ++i) {
            Map<Long, Long> cr = new HashMap<>();

            if (nums[i] == k) {
                ++cnt;
            }
            cr.put((long) nums[i], 1L);

            for (Map.Entry<Long, Long> e : pr.entrySet()) {
                long v = e.getKey();
                long f = e.getValue();
                long newAnd = v & nums[i];
                if (newAnd == k) {
                    cnt += f;
                }
                cr.put(newAnd, cr.getOrDefault(newAnd, 0L) + f);
            }

            pr = cr;
        }

        return cnt;
    }
}
