class Solution {
    #define ll long long
public:
    long long countSubarrays(vector<int>& a, int k) {
        int n = a.size();
        ll ans = 0;
        vector<vector<int>> pos(32);
        for(int i = 0; i < 31; i++) pos[i].push_back(n);
        for(int i = n - 1; i >= 0; i--) {
            for(int j = 0; j < 31; j++) {
                if(~ a[i] >> j & 1) {
                    pos[j].push_back(i);
                }
            }
        }
        for(int i = 0; i < n; i++) {
            int mx = i , mn = n;
            for(int j = 0; j < 31; j++) {
                if(k >> j & 1) {
                    mn = min(mn, pos[j].back());
                } else {
                    mx = max(mx, pos[j].back());
                }
            }
            ans += max(0, mn - mx);
            for(int j = 0; j < 31; j++) {
                if(~ a[i] >> j & 1) {
                    pos[j].pop_back();
                }
            }
        }
        return ans;
    }
};
