class Solution {
public:
    long long countSubarrays(vector<int>& A, int k) {
        typedef long long LL;
        LL ans = 0;
        int n = A.size();
        vector<pair<int, int> > stk;
        for(int i = 0; i < n; ++i) {
            int m = 0, x = A[i];
            stk.push_back({x, i});
            for(auto &it: stk) {
                it.first &= x;
                if((it.first & k) == k && (!m || it.first != stk[m - 1].first))
                    stk[m++] = it;
            }
            stk.resize(m);
            if(m && stk[0].first == k)
                ans += (m > 1 ? stk[1].second : i + 1) - stk[0].second;
        }
        return ans;
    }
};
