class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
    	vector<int>a=nums;
        int n=a.size();
        vector<array<int,2>>vec;
        long long ans=0;
        for(int i=0;i<n;i++){
        	vec.push_back({a[i],i});
        	for(auto &[v,p]:vec){
        		v=a[i]&v;
			}
			int len=vec.size();
			vector<array<int,2>>tmp;
			for(int l=0,r;l<len;l=r+1){
				r=l;
				while(r+1<len && vec[r+1][0]==vec[l][0])++r;
				tmp.push_back(vec[l]);
			}
			vec=tmp;
			len=vec.size();
			int lst=i+1;
			for(int j=len-1;j>=0;j--){
				if(vec[j][0]==k){
					ans+=lst-vec[j][1];
				}
				lst=vec[j][1];
			}
		}
		return ans;
    }
};
