class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        i = 0
        total = 0
        last = [-inf] * 30
        for j, b in enumerate(nums):
            if b & k != k:
                last = [-inf] * 30
                i = j + 1
                continue

            i2 = j
            for i1 in range(30):
                bit = 1 << i1
                if bit & k == 0:
                    if bit & b == 0:
                        last[i1] = j
                    else:
                        i2 = min(last[i1], i2)
            if i2 != -inf:
                total += i2 + 1 - i

        return total
