class Solution {
public:
long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0;
        vector<int>vals(32);
        vector<int>vals2(32);
        int i=0;
        bool zero=false;
        if(k==0)
        {
            zero=true;
        }
        while(k>0)
        {
        	vals[i]=k%2;
        	k/=2;
        	i++;
        }
        for(int i=0; i<nums.size(); i++)
        {
        	for(int j=0; j<32; j++)
        	{
        		if(nums[i]%2==1)
        		{
        			vals2[j]++;
        		}
        		else
        		{
        			vals2[j]=0;
        		}
                nums[i]/=2;
        	}
        	int same=INT_MAX;
        	int no=0;
        	for(int j=0; j<32; j++)
        	{
        		if(vals[j]==1)
        		{
        			same=min(vals2[j],same);
        		}
        		else
        		{
        			no=max(no,vals2[j]);
        		}
        	}
            if(zero)
            {
                ans+=i-no+1;
            }
            else
        	ans+=max(0,same-no);
        }
        return ans;
    }
};
