class Solution {
public:
    void fun(int a){
        a++;
    }
    void xyz(int a){
        a--;
    }
    long long countSubarrays(vector<int>& nums, int k) {
          int n = nums.size();
        long long ans = 0;
        fun(1);
        unordered_map<long long, long long> pr;
        xyz(1);
        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> cr; 

            if (nums[i] == k) {
                ++ans;
            }
            cr[nums[i]] = 1;

            for (auto& [val, freq] : pr) {
                long long nr = val & nums[i];
                if (nr == k) {
                    ans += freq;
                }
                cr[nr] += freq;
            }

            pr = cr;
        }
        int ppp=0;
        for(int i=0;i<2;i++){
            ppp++;
        }
        fun(1);
        return ans;
    }
};
