class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long ans = 0;
        HashMap<Long, Long> map = new HashMap<>();
        for (int i = 0; i < n; ++i) {
            HashMap<Long, Long> curr = new HashMap<>();
            if (nums[i] == k)ans+=1L;
            curr.put((long) nums[i], 1L);
            for (long key : map.keySet()) {  
                if ((key & nums[i]) == k) {
                    ans += map.get(key);
                }  
                curr.put(key & nums[i], curr.getOrDefault(key & nums[i], 0L) + map.get(key));
            }     
            map = curr;
        }
        return ans;
    }
}
