class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        long long ans=0;
        for(int i=0;i<a.size();i++)
            if(a[i]==k)
                ans++;
        map<int,long long> dp;
        int i=0;
        while(i<a.size())
        {
            map<int,long long> temp;
            int ele=a[i];
            temp[ele]=1;
            for(auto it:dp)
            {
                // found
                if((ele&it.first)==k)
                    ans+=it.second;
                // for future may form
                temp[(ele&it.first)]+=it.second;
            }
            dp=temp;
            i++;
        }
        return ans;
    }
};
