#define ll long long

class Solution {
public:
    ll countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll cnt = 0;
        unordered_map<ll, ll> prev;

        int i = 0;
        while (i < n) {
            unordered_map<ll, ll> curr;

            ll v = nums[i];

            if (v == k) {
                ++cnt;
            }
            curr[v] += 1;

            for (auto& [p, f] : prev) {
                ll r = p & v;
                if (r == k) {
                    cnt += f;
                }
                curr[r] += f;
            }

            prev = curr;
            ++i;
        }

        return cnt;
    }
};
