class Solution {
public:
     long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;

        unordered_map<long long, long long> prev;
         int i=0;
        while(i<n) {
            unordered_map<long long, long long> current; 

            if (nums[i] == k) {
                ++count;
            }
            current[nums[i]] = 1;

            for (auto& [val, freq] : prev) {
                long long newAndResult = val & nums[i];
                if (newAndResult == k) {
                    count += freq;
                }
                current[newAndResult] += freq;
            }

            prev = current;
            i++;
        }

        return count;
    }
};
