class Solution {
public:

    #define ll long long

    long long countSubarrays(vector<int>& a, int k) {
        ll n=a.size();
        ll x=(1ll<<32)-1;
        ll ans=0;
        vector<ll> pre(32,0);
        for(int i=0;i<n;i++) {
            x&=a[i];
            bool f=1;
            ll cnt=i+1;
            ll maxi=0;
            for(int j=0;j<32;j++) {
                // cout<<pre[j]<<" ";
                if(((a[i]>>j)&1)) {
                    // cout<<pre[j]<<" ";
                    pre[j]++;
                }
                else {
                    pre[j]=0;
                }
                bool f1=((k>>j)&1);
                bool f2=((x>>j)&1);
                if(f1==0 && f2==1) {
                    f=0;
                }
                else if(f1==1 && f2==0) {
                    cnt=min(cnt,pre[j]);
                }
                else if(f1==0 && f2==0) {
                    maxi=max(maxi,pre[j]);
                    // cout<<pre[j]<<" ";
                }
            }
            // cout<<endl;
            if(f) {
                if(maxi<cnt) {
                    ans+=(cnt-maxi);
                }
            }
            // cout<<ans<<" "<<maxi<<endl;
        }
        cout<<endl;
        return ans;
    }
};
