class Solution {
public:

    long long countSubarrays(vector<int>& nums, int k) {
        map<int, long long> count;
        set<int> s;
        long long result = 0;

        for (int i = 0; i < nums.size(); i++) {
            set<int> next;
            map<int, long long> mp;
            next.insert(nums[i]);
            mp[nums[i]]++;
            for (auto x : s) {
                int new_and = nums[i] & x;
                mp[new_and] += count[x];
                next.insert(new_and);
            }

            if (mp.find(k) != mp.end()) {
                result += mp[k];
            }
            s = next;
            count = mp;
        }

        return result;
    }
};
