class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        long long ans = 0; 
        unordered_map<int, long long> oldmp;
        for (int i = 0; i <nums.size(); i++) {
            int currelement=nums[i];

            currelement==k?ans+=1:ans+=0;
            unordered_map<int, long long> newmp; 
            newmp[currelement] = 1;
            for (auto it=oldmp.begin();it !=oldmp.end();it++) {
                long long curans = it->first & currelement;
                if (curans == k) {
                    ans += it->second;
                }
                newmp[curans] += it->second;
            }

            oldmp = newmp;
        }
        return ans;
    }
};
