class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        long long res = 0;
        int n = nums.size();
        int dp[n][32];
        for(int i=0;i<n;i++){
            for(int j =0;j<32;j++)
                dp[i][j] = -1;
        }
        // ones min
        // zeros max
        for(int i=0;i<n;i++)
        {
             int onemin = i+1;
             int zero = 1;
            for(int j=0;j<32;j++)
            {


                   int set = 0;
                    if( (1LL<<j)&k )
                        set = 1;

                   int el  = 0;
                   if((1LL<<j)&nums[i])
                       el = 1;

                   if(i==0){
                       if(set){
                           if(el){
                               dp[i][j]  = 1;
                           } else {
                               dp[i][j]  = 0;
                           }
                       }else{
                          if(el){
                               dp[i][j]  = n;
                          } else {
                              dp[i][j]  = 1;
                          }        
                       }
                   } else {
                       if(set){
                           if(el){
                               dp[i][j]  = 1+dp[i-1][j];
                           } else {
                               dp[i][j]  = 0;
                           }
                       }else{
                          if(el){
                               dp[i][j]  = dp[i-1][j]+1;
                          } else {
                              dp[i][j]  = 1;
                          }        
                       }
                   }
                 // cout<<dp[i][j]<<" ";

                if(set){
                    onemin = min(onemin, dp[i][j]) ;
                } else {
                    zero = max(zero, dp[i][j]);
                }
            }
            // cout<<endl;

                int temp = onemin-zero+1;
                // cout<<temp<<endl;
                if(temp>0)
                res += temp;




        }
        return res;


    }
};
