class Solution {
public:
    long long countSubarrays(vector<int>& p, int k) {
        vector<int> bt(31,-1);
        int n = p.size();
        long long ans = 0;
        for(int i = 0;i < n;i++){
            int minm = n,maxm = -1;
            for(int j = 0;j < 31;j++){
                if(!((p[i] >> j)&1)){
                    bt[j] = i;
                }
                if((k >> j)&1){
                    maxm = max(maxm,bt[j]);
                }else{
                    minm = min(minm,bt[j]);
                }
            }
            if(minm != -1){
                ans += max(0,minm-maxm);
            }
        }
        return ans;
    }
};
