class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
       vector<vector<int>> v;
       vector<int>t;
       long long ans = 0;
       for(auto &i:nums)
        {
            if((i & k) == k)
            {
                t.push_back((i ^ k));
            }
            else if(t.size())
            {
                v.push_back(t);
                t.clear();
            }
        }
        function<int(vector<vector<int>>&,int,int)> fun_and = [&](vector<vector<int>>&bh,int s,int e)      {    
            int a = 0;
            for(int i = 0;i < 30;i++){
                if(bh[e][i] - bh[s][i] == e - s){
                    a += (1<<i);
                }
            }
            return a;
        };
        if(t.size())
        v.push_back(t);
        for(auto &i:v){
            vector<vector<int>>bh(i.size() + 1,vector<int>(30,0));
            for(int j = 0;j < i.size();j++)
            {
                for(int r = 0;r < 30;r++){
                    bh[j + 1][r] += bh[j][r];
                    if((i[j] & (1<<r)))
                    bh[j + 1][r]++;
                }
            }
            int s = 0,e = 1;
            for(int s = 0;s < i.size();s++){
                while(e < i.size() && fun_and(bh,s,e) != 0)
                e++;
                if(fun_and(bh,e,s) == 0)
                {
                    ans += i.size() - e + 1;
                }
            }
        }
        return ans;
    }
};
