class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        int n = nums.size();
        long long ans = 0;

        unordered_map<long long, long long> prev;
        for (int i=0;i<n;i++) {

            unordered_map<long long, long long> curr; 
            if (nums[i] == k) {
                ++ans;
            }

            curr[nums[i]]++;

            for (auto& [value, frequency] : prev) {
                long long finalans = value & nums[i];
                if (finalans == k) {
                    ans += frequency;
                }
                curr[finalans] += frequency;
            }

            prev = curr;
        }
        return ans;
    }
};
