class Solution {
    public long countSubarrays(int[] varna, int yuga) {
        int prithvi = varna.length;
        long ganana = 0;
        Map<Long, Long> purvaPhal = new HashMap<>();

        for (int i = 0; i < prithvi; ++i) {
            Map<Long, Long> vartamanPhal = new HashMap<>();
            if (varna[i] == yuga) {
                ++ganana;
            }
            vartamanPhal.put((long) varna[i], 1L);
            for (Map.Entry<Long, Long> entry : purvaPhal.entrySet()) {
                long val = entry.getKey();
                long freq = entry.getValue();
                long nayaAndPhal = val & varna[i];
                if (nayaAndPhal == yuga) {
                    ganana += freq;
                }
                vartamanPhal.put(nayaAndPhal, vartamanPhal.getOrDefault(nayaAndPhal, 0L) + freq);
            }
            purvaPhal = vartamanPhal;
        }

        return ganana;
    }
}
