public class Solution {
    public long countSubarrays(int[] nums, int k) {
        int length = nums.length;
        long subarrayCount = 0;

        java.util.Map<Long, Long> previousResults = new java.util.HashMap<>();

        for (int i = 0; i < length; ++i) {
            java.util.Map<Long, Long> currentResults = new java.util.HashMap<>();

            if (nums[i] == k) {
                ++subarrayCount;
            }
            currentResults.put((long) nums[i], 1L);

            for (java.util.Map.Entry<Long, Long> entry : previousResults.entrySet()) {
                long value = entry.getKey();
                long frequency = entry.getValue();

                long newAndResult = value & nums[i];
                if (newAndResult == k) {
                    subarrayCount += frequency;
                }
                currentResults.put(newAndResult, currentResults.getOrDefault(newAndResult, 0L) + frequency);
            }

            previousResults = currentResults;
        }

        return subarrayCount;
    }
}
