class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;
        unordered_map<long long, long long> prev_R;

        for (int i = 0; i < n; ++i) {
            pCN(nums[i], k, cnt, prev_R);
        }

        return cnt;
    }

private:
    void pCN(int num, int k, long long& count, unordered_map<long long, long long>& prevResults) {
        count += cnt_SV(num, k);
        prevResults = u_PR(prevResults, num, k, count);
    }

    long long cnt_SV(int num, int k) {
        return (num == k) ? 1 : 0;
    }

    unordered_map<long long, long long> u_PR(
        unordered_map<long long, long long>& prevResults, int num, int k, long long& count) {

        unordered_map<long long, long long> currResults;
        int_PR(currResults, num);
        p_PR(prevResults, num, k, count, currResults);

        return currResults;
    }

    void int_PR(unordered_map<long long, long long>& currResults, int num) {
        currResults[num] = 1;
    }

    void p_PR(
        unordered_map<long long, long long>& prevResults, int num, int k, long long& count, unordered_map<long long, long long>& currResults) {

        for (auto& [val, freq] : prevResults) {
            long long newAndResult = computeAndResult(val, num);
            u_CIM(newAndResult, k, freq, count);
            u_CM(currResults, newAndResult, freq);
        }
    }

    long long computeAndResult(long long val, int num) {
        return val & num;
    }

    void u_CIM(long long newAndResult, int k, long long freq, long long& count) {
        if (newAndResult == k) {
            count += freq;
        }
    }

    void u_CM(unordered_map<long long, long long>& currResults, long long newAndResult, long long freq) {
        currResults[newAndResult] += freq;
    }
};
