class Solution {
    int freq[][],n,k;
    public long countSubarrays(int[] nums, int k) {
        n = nums.length; 
        this.k = k;
        long res = 0;
        freq = new int[n+1][32];

        for(int i = 0; i<n; i++){
            for(int j = 0; j<32; j++){
                 int mask = (1<<j);
                 int add = (mask & nums[i]) == 0?0:1;
                 freq[i+1][j] = freq[i][j]+add;
            }
        }

        for(int i = 0; i<n; i++){
            if(k>nums[i])continue; 
            res += fun(i);
    }

         return res;
}


    int fun(int st){
        int l = st,h = n; 

        while(h-l>1){
            int m = (l+h)>>1; 
            int num = cal(st,m);
            if(num>k)l = m;
            else h = m;
        }

        if(cal(st,h) != k)return 0;
        int low = st,high = n+1;
        while(high-low>1){
            int m = (low+high)>>1;
            int num = cal(st,m);
            if(num >= k)low = m;
            else high = m;
        }

        // System.out.println(h+" "+low+" "+cal(0,low));
        return low-h+1;
    }

    int cal(int l, int h){
        int res = 0; 
        for(int i = 0; i<32; i++){
            if(freq[h][i]-freq[l][i] == h-l)res += (1<<i);
    }
         return res;
}

}
