class Solution {
public:
    long long countSubarrays(vector<int>& vecnum, int k) {
        int b=vecnum.size();
    long long ans=0;
    unordered_map<int,int> andmap;
    for(int p=0;p<b;p++) {
        unordered_map<int, int> newmap;
        for (auto it:andmap) {
            int val=it.first;
            int ment=it.second;
            int curand=val&vecnum[p];
            if (curand==k){
                ans+=ment;
            }
            newmap[curand]+=ment;
        }
        newmap[vecnum[p]] += 1;
        if (vecnum[p]==k) {
            ans++;
        }
        andmap=newmap;
    }
    return ans;
    }
};
