class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        int n = nums.size();

        unordered_map < long long, long long > prev, curr;
        long long cnt = 0;

        for (auto num: nums) {

            curr[num] = 1;

            for (auto i: prev) {

                long long AND = (i.first & num);
                curr[AND] += i.second;
                cnt += ((AND == k) ? i.second: 0);

            }

            prev = move(curr);
            curr.clear();

        }

        return cnt + count(nums.begin(), nums.end(), k);
    }
};
