import java.util.HashMap;
import java.util.Map;

class Solution {
    public long countSubarrays(int[] arrayOfNumbers, int targetValue) {
        int lengthOfArray = arrayOfNumbers.length;
        long totalCountOfSubarrays = 0;
        Map<Long, Long> previousAndResults = new HashMap<>();
        for (int currentIndex = 0; currentIndex < lengthOfArray; ++currentIndex) {
            Map<Long, Long> currentAndResults = new HashMap<>();
            if (arrayOfNumbers[currentIndex] == targetValue) {
                ++totalCountOfSubarrays;
            }
            currentAndResults.put((long) arrayOfNumbers[currentIndex], 1L);
            for (Map.Entry<Long, Long> entry : previousAndResults.entrySet()) {
                long previousValue = entry.getKey();
                long frequency = entry.getValue();
                long newAndResult = previousValue & arrayOfNumbers[currentIndex];
                if (newAndResult == targetValue) {
                    totalCountOfSubarrays += frequency;
                }
                currentAndResults.put(newAndResult, currentAndResults.getOrDefault(newAndResult, 0L) + frequency);
            }

            previousAndResults = currentAndResults;
        }

        return totalCountOfSubarrays;
    }
}
